import React from "react";
import { Card, CardContent, Typography, Grid, Box } from "@mui/material";

const blogPosts = [
  {
    id: 1,
    title: "Understanding React Hooks",
    summary:
      "This post discusses the ins and outs of React Hooks, a powerful feature introduced in React 16.8 for managing state and side effects in function components.",
    date: "April 15, 2024",
  },
  {
    id: 2,
    title: "Why TypeScript is Essential for Large Projects",
    summary:
      "Explore why TypeScript is becoming essential for more robust, less error-prone code in large-scale applications.",
    date: "March 30, 2024",
  },
  // Add more blog posts as needed
];

const Blog = () => {
  return (
    <Box p={3}>
      <Typography variant="h4" gutterBottom>
        Blog
      </Typography>
      <Grid container spacing={2}>
        {blogPosts.map((post) => (
          <Grid item xs={12} sm={6} md={4} key={post.id}>
            <Card>
              <CardContent>
                <Typography variant="h5" component="div">
                  {post.title}
                </Typography>
                <Typography color="text.secondary" gutterBottom>
                  {post.date}
                </Typography>
                <Typography variant="body2">{post.summary}</Typography>
              </CardContent>
            </Card>
          </Grid>
        ))}
      </Grid>
    </Box>
  );
};

export default Blog;
