import React, { useState, useEffect } from "react";
import { useParams } from "react-router-dom";
import { Box, Typography } from "@mui/material";
import axios from "axios";
import { marked } from "marked";
import hljs from "highlight.js";

const PostDetail = () => {
  const { postId } = useParams();
  const [post, setPost] = useState({ title: "", content: "" });

  useEffect(() => {
    marked.setOptions({
      renderer: new marked.Renderer(),
      highlight: function (code, lang) {
        const language = hljs.getLanguage(lang) ? lang : "plaintext";
        return hljs.highlight(code, { language }).value;
      },
      langPrefix: "hljs language-", // CSS classes for highlight.js
      breaks: true,
      gfm: true,
    });

    const fetchPost = async () => {
      const url = `https://api.github.com/repos/sebastianberge/blog-posts/contents/posts/${postId}.md`;
      const response = await axios.get(url);
      const content = atob(response.data.content); // Decode base64
      const htmlContent = marked(content); // Convert Markdown to HTML
      setPost({ title: postId, content: htmlContent });
    };

    fetchPost();
  }, [postId]);

  if (!post.content) return <Typography>Loading...</Typography>;

  return (
    <Box p={3}>
      <Typography variant="h4" gutterBottom>
        {post.title}
      </Typography>
      <div dangerouslySetInnerHTML={{ __html: post.content }} />
    </Box>
  );
};

export default PostDetail;
